import React, { Component } from 'react';
import { Link, Navigate } from 'react-router-dom';
import axios from 'axios';

class SignIn extends Component {
  state = {
    name: '',
    email: '',
    password: '',
    showSubmitError: false,
    showLoginPage: false,
    errorMsg: ''
  };

  async componentDidMount () {
     const option = {
      headers: {
          Authorization: `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6Niwicm9sZSI6ImVtcGxveWVlIiwiaWF0IjoxNzIyMDg3ODI1LCJleHAiOjE3MjQ2Nzk4MjV9.IIStVrm1-7QuTVkeMVbD99j5_ImVAHN9CxXNW4QpNNA`,
      },
      method: 'GET',
  }
      const response = await fetch('http://localhost:5000/api/employees/', option);
      const data = await response.json()
      console.log(data)
}
  
onChangEemail = (event) => {
    this.setState({ userName: event.target.value });
  };

  onChangePassword = (event) => {
    this.setState({ password: event.target.value });
  };

  onChangeName = (event) => {
    this.setState({ name: event.target.value });
  };

  submitForm = async (event) => {
    event.preventDefault();
    const { name, password, email } = this.state;

    if (email === '' || password === '' || name === '') {
      this.setState({ showSubmitError: true, errorMsg: 'Please fill in all fields' });
    } else {
      const userData = { name, email, password };

      const options = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(userData)
      };
      const response = await fetch('http://localhost:5000/api/auth/register', options);
      console.log(response)
      console.log('akshay')
    }
  }

  render() {
    const { email, password, name, showSubmitError, errorMsg, showLoginPage } = this.state;

    if (showLoginPage) {
      return <Navigate to='/login' replace={true} />;
    }

    return (
      <div className="container d-flex justify-content-center flex-column">
        <Link to='/login'>
          <button>Login</button>
        </Link>
        <form onSubmit={this.submitForm} className='container'>
          <legend>Sign In</legend>
          <div className="mb-3" style={{ width: '500px' }}>
            <label htmlFor="name" className="form-label">Name</label>
            <input type="text" id="name" className="form-control" placeholder="Name" onChange={this.onChangeName} value={name} />
          </div>
          <div className="mb-3" style={{ width: '500px' }}>
            <label htmlFor="email" className="form-label">email</label>
            <input type="text" id="email" className="form-control" placeholder="email" onChange={this.onChangEmail} value={email} />
          </div>
          <div className="mb-3" style={{ width: '500px' }}>
            <label className="form-label" htmlFor="password">Password</label>
            <input className="form-control" type="password" id="password" placeholder="Password" onChange={this.onChangePassword} value={password} />
          </div>
          <button type="submit" className="btn btn-primary">Sign In</button>
          {showSubmitError && <p style={{ color: 'red' }}>{errorMsg}</p>}
        </form>
      </div>
    );
  }
}

export default SignIn;
