import {React,useState,useEffect} from "react";
import AuthContext from "./AuthContext";
import Cookies from 'js-cookie';

export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated, userAdmin] = useState(false);

    useEffect(() => {
        // Check if token exists in cookies and set authentication state
        const jwtToken = Cookies.get('jwt_token');
        if (jwtToken === undefined){
            setIsAuthenticated(!!jwtToken);
        }
        
    }, []); // Empty dependency array ensures this effect runs only once

    // Function to log in
    const login = (token, role) => {
        Cookies.set('jwt_token', token, { expires: 5 }); // Expires in 5 days
        setIsAuthenticated(true);
        if (role === 'admin'){
            userAdmin(true)
        }
         userAdmin(false)
      };
    

    // Function to log out
    const logout = () => {
        Cookies.remove('jwt_token');
        setIsAuthenticated(false);
    };

    return (
        <AuthContext.Provider value={{ login, logout, isAuthenticated, setIsAuthenticated, userAdmin }}>
            {children}
        </AuthContext.Provider>
    );
};
