import React, { Component } from 'react';
import Cookies from 'js-cookie';

const apiStatusConstants = {
    initial: 'INITIAL',
    success: 'SUCCESS',
    failure: 'FAILURE',
    inProgress: 'IN_PROGRESS',
}
class EmployeeDelete extends Component {
    state = {
        employees: [],
        apiStatus: apiStatusConstants.initial,
    }

    componentDidMount = () => {
        this.fetchEmployee();
    }

    fetchEmployee = async () => {
        const jwtToken = Cookies.get('jwt_token');
        this.setState({
            apiStatus: apiStatusConstants.inProgress,
        });

        const option = {
            headers: {
                Authorization: `Bearer ${jwtToken}`,
            },
            method: 'GET'
        };

        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/employees`, option);
            if (!response.ok) {
                this.setState({
                    apiStatus: apiStatusConstants.failure,
                });
                throw new Error('Error fetching employees');
            }

            const contentType = response.headers.get("content-type");
            if (!contentType || !contentType.includes("application/json")) {
                this.setState({
                    apiStatus: apiStatusConstants.failure,
                });
                throw new Error("Response is not JSON");
            }

            const data = await response.json();
            console.log(data)
            this.setState({ employees: data, apiStatus: apiStatusConstants.success });
        } catch (error) {
            console.error(error);
            this.setState({
                apiStatus: apiStatusConstants.failure,
            });
        }
    }
   
    handleChangeId = (event) => {
        this.setState({ id: event.target.value });
    };

    handleSubmitDelete = async (event) => {
        event.preventDefault();
        const { id } = this.state;
        const jwtToken = Cookies.get('jwt_token')
        const options = {
            method: 'DELETE',
            headers: {
                Authorization: `Bearer ${jwtToken}`,
                'Content-Type': 'application/json'
            }
        };

        try {
            const response = await fetch(`/employee/api/${id}`, options); // Replace "/employee/api/" with your actual delete endpoint
            if (!response.ok) {
                throw new Error('Delete request failed');
            }
            alert(`Employee id: ${id} Data Delete Successfully`)
            this.setState({ id: '' })
            // Optionally, you can update the UI to reflect the deletion
        } catch (error) {
            console.error('Error deleting employee:', error);
        }
    };

    render() {
        const { id } = this.state;

        return (
            <div className='employee_Bg_Container'>
                <h2>Employee List</h2>
                <div className='employee_Container'>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Position</th>
                                <th>Department</th>
                                <th>Created At</th>
                                <th>Updated At</th>
                            </tr>
                        </thead>
                        <tbody>
                            {employees.map(employee => (
                                <tr key={employee.id}>
                                    <td>{employee.id}</td>
                                    <td>{employee.name}</td>
                                    <td>{employee.position}</td>
                                    <td>{employee.department}</td>
                                    <td>{employee.createdAt}</td>
                                    <td>{employee.updatedAt}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default EmployeeDelete;
