import React, { Component } from 'react';
import Cookies from 'js-cookie';
class EmployeeDelete extends Component {
  state = {
    id: ''
  };

  handleChangeId = (event) => {
    this.setState({ id: event.target.value });
  };

  handleSubmitDelete = async (event) => {
    event.preventDefault();
    const { id } = this.state;
    const jwtToken = Cookies.get('jwt_token')
    const options = {
      method: 'DELETE',
      headers: {
        Authorization: `Bearer ${jwtToken}`,
        'Content-Type': 'application/json'
      }
    };

    try {
      const response = await fetch(`/employee/api/${id}`, options); // Replace "/employee/api/" with your actual delete endpoint
      if (!response.ok) {
        throw new Error('Delete request failed');
      }
      alert(`Employee id: ${id} Data Delete Successfully`)
      this.setState({id: ''})
      // Optionally, you can update the UI to reflect the deletion
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  render() {
    const { id } = this.state;

    return (
      <div className="container mb-5">
        <h2 style={{ fontFamily: 'Arial, sans-serif', color: '#333', marginBottom: '20px' }}>Delete Employee</h2>
        <form onSubmit={this.handleSubmitDelete}>
          <div className="form-group">
            <label htmlFor="id" style={{ fontFamily: 'Arial, sans-serif', color: '#333', fontWeight: 'bold' }}>Employee ID:</label>
            <input
              type="text"
              className="form-control"
              id="id"
              name="id"
              value={id}
              onChange={this.handleChangeId}
              required
              style={{ fontFamily: 'Arial, sans-serif', color: '#333', fontWeight: 'normal', borderRadius: '5px', width: "500px" }}
            />
          </div>
          <button type="submit" className="btn btn-danger" style={{ fontFamily: 'Arial, sans-serif', fontWeight: 'bold', backgroundColor: '#dc3545', borderColor: '#dc3545' }}>Delete Employee</button>
        </form>
      </div>
    );
  }
}

export default EmployeeDelete;
