import React, { Component } from 'react';
import Cookies from 'js-cookie';
import Headers from '../Header/Header';

class EmployeeCreate extends Component {
    state = {
        name: '',
        position: '',
        department: '',
    };

    onChangeName = (event) => {
        this.setState({ name: event.target.value });
    };

    onChangePosition = (event) => {
        this.setState({ position: event.target.value });
    };

    onChangeDepartment = (event) => {
        this.setState({ department: event.target.value });
    };

    onSubmitAdd = async (event) => {
        event.preventDefault();
        const { name, position, department } = this.state;
        const employeeDetails = { name, position, department };
        const jwtToken = Cookies.get('jwt_token');
        const options = {
            method: 'POST',
            headers: {
                Authorization: `Bearer ${jwtToken}`,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(employeeDetails)
        };

        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/employees`, options);
            if (!response.ok) {
                throw new Error('Response post not ok');
            }
            alert(`Employee ${name}: Added Successfully`);
            this.setState({ name: '', position: '', department: '' });
        } catch (error) {
            console.error(error);
        }
    };

    render() {
        const { name, position, department } = this.state;

        const containerStyle = {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            padding: '20px',
            backgroundColor: '#f8f9fa',
            minHeight: '100vh',
        };

        const formStyle = {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            width: '100%',
            maxWidth: '500px',
            padding: '20px',
            backgroundColor: '#fff',
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        };

        const headingStyle = {
            fontFamily: 'Roboto, sans-serif',
            color: '#333',
            marginBottom: '20px',
            textAlign: 'center',
        };

        const labelStyle = {
            fontFamily: 'Roboto, sans-serif',
            color: '#333',
            fontWeight: 'bold',
            marginBottom: '5px',
            alignSelf: 'flex-start',
        };

        const inputStyle = {
            fontFamily: 'Roboto, sans-serif',
            color: '#333',
            padding: '10px',
            border: '1px solid #ccc',
            borderRadius: '5px',
            width: '100%',
            marginBottom: '15px',
        };

        const buttonStyle = {
            fontFamily: 'Roboto, sans-serif',
            fontWeight: 'bold',
            backgroundColor: '#007bff',
            borderColor: '#007bff',
            color: '#fff',
            padding: '10px 20px',
            borderRadius: '5px',
            border: 'none',
            cursor: 'pointer',
            transition: 'background-color 0.3s ease',
        };

        const buttonHoverStyle = {
            backgroundColor: '#0056b3',
        };

        return (
            <div>
                <Headers />
                <div style={containerStyle}>
                    <h2 style={headingStyle}>Add Employee</h2>
                    <form onSubmit={this.onSubmitAdd} style={formStyle}>
                        <div className="form-group" style={{ width: '100%' }}>
                            <label htmlFor="name" style={labelStyle}>Name:</label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                value={name}
                                onChange={this.onChangeName}
                                required
                                style={inputStyle}
                            />
                        </div>
                        <div className="form-group" style={{ width: '100%' }}>
                            <label htmlFor="position" style={labelStyle}>Position:</label>
                            <input
                                type="text"
                                id="position"
                                name="position"
                                value={position}
                                onChange={this.onChangePosition}
                                required
                                style={inputStyle}
                            />
                        </div>
                        <div className="form-group" style={{ width: '100%' }}>
                            <label htmlFor="department" style={labelStyle}>Department:</label>
                            <input
                                type="text"
                                id="department"
                                name="department"
                                value={department}
                                onChange={this.onChangeDepartment}
                                required
                                style={inputStyle}
                            />
                        </div>
                        <button
                            type="submit"
                            style={buttonStyle}
                            onMouseOver={e => e.currentTarget.style.backgroundColor = buttonHoverStyle.backgroundColor}
                            onMouseOut={e => e.currentTarget.style.backgroundColor = buttonStyle.backgroundColor}
                        >
                            Add Employee
                        </button>
                    </form>
                </div>
            </div>
        );
    }
}

export default EmployeeCreate;
