import { Component } from 'react'
import {Link, Navigate} from 'react-router-dom'

class Singin extends Component {
  state ={
    username: '',
    password: '',
    address: '',
    showSubmitError: true,
    showLoginPage: false,
    errorMsg: ''
  }
  onChangeUsername = (event) =>{
    this.setState({username: event.target.value})
  }
  onChangePassword = (event) =>{
    this.setState({password: event.target.value})
  }
  onChangeAddress = (event) =>{
    this.setState({address: event.target.value})
  }

  submitForm = async (event)  =>{
   event.preventDefault()
   const {username, password, address, showSubmitError, errorMsg} = this.state
   const userDetails = {username, password, address}

   if (username === '' || password === '' || address ===''){
    this.setState({errorMsg: 'Fild a valid text'})
   }
   else if (username !== '' && password !== '' && address !== '') {
    const option = {
      method: 'POST',
      headers: {
        "content-type": 'application/json'
      },
      body: JSON.stringify(userDetails),
    }
    const response = await fetch('/singin', option)
   
    if(response.ok){
      this.setState({showLoginPage: true})
    }
    else{
      const message =response.json()
      this.setState({showSubmitError: true, errorMsg: ''})
    }
   }
   this.setState({username:'', password: '', address: ''})
  }

  render() {
    const {username,password,address,showSubmitError,errorMsg, showLoginPage} = this.state
    if(showLoginPage === true){
      return <Navigate to="/login" replace={true} />
    }
    return (
      <div className="container d-flex justify-content-center flex-column">
        <Link to='/login'>
          <button>Login</button>
        </Link>
        <form onSubmit={this.submitForm} className='container'>
          <legend>Sing in</legend>
          <div className="mb-3" style={{ width: `500px` }}>
            <label htmlFor="UserName" className="form-label">UserName</label>
            <input type="text" id="UserName" className="form-control" placeholder="UserName" onChange={this.onChangeUsername} value={username} />
          </div>
          <div className="mb-3" style={{ width: `500px` }}>
            <label className="form-label" htmlFor="password">
              password
            </label>
            <input className="form-control" type="password" id="password" placeholder="password" onChange={this.onChangePassword} value={password} />

          </div>
          <div className="mb-3" style={{ width: `500px` }}>
            <label htmlFor="address" className="form-label">address</label>
            <input type="text" id="address" className="form-control" placeholder="address" onChange={this.onChangeAddress} value={address} />
          </div>
          <button type="submit" className="btn btn-primary" >Sing in</button>
          {showSubmitError ? <p style={{ color: 'red' }}>{errorMsg}</p> : null}
        </form>
      </div>
    )
  }
}

export default Singin
