import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './SignInPage.css';
//import backgroundImage from '../assets/background-image.jpg'; // Update path

const SignInPage = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignIn = async (e) => {
    e.preventDefault();
    try {
      const apiUrl = `${process.env.REACT_APP_API_URL}/api/auth/register`;
      const response = await axios.post(apiUrl, { name, email, password });
      console.log('Response:', response);
      if (response.status === 201) {
        navigate('/');
      } else {
        setError(response.data.message || 'Login failed');
      }
    } catch (error) {
      console.error('Error:', error);
      setError(error.response.data.message || 'An error occurred');
    }
  };

  const onClickLogin = () => {
    navigate('/')
  }

  return (
    <div className="sign-in-container">
      <div
        className="background-image"

      ></div>
      <div className="form-wrapper">
        <h1>Sign In</h1>
        <form onSubmit={handleSignIn}>
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}

            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}

            />
          </div>
          {error && <p className="error-message">{error}</p>}
          <button type="submit" style={{ backgroundColor: '#e2a54a' }}>Sign Up</button>
        </form>
        <button style={{ marginTop: '5px' }} onClick={onClickLogin}>Login</button>
      </div>

    </div>
  );
};

export default SignInPage;
