import React, { Component } from 'react';
import { Link, Navigate } from 'react-router-dom';
import axios from 'axios';

class SignIn extends Component {
  state = {
    name: '',
    userName: '',
    password: '',
    showSubmitError: false,
    showLoginPage: false,
    errorMsg: ''
  };

  componentDidCatch

  onChangeUserName = (event) => {
    this.setState({ userName: event.target.value });
  };

  onChangePassword = (event) => {
    this.setState({ password: event.target.value });
  };

  onChangeName = (event) => {
    this.setState({ name: event.target.value });
  };

  submitForm = async (event) => {
    event.preventDefault();
    const { name, password, userName } = this.state;

    if (userName === '' || password === '' || name === '') {
      this.setState({ showSubmitError: true, errorMsg: 'Please fill in all fields' });
    } else {
      const userData = { name, userName, password };
      const options = {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        },
       // body: JSON.stringify(userData)
      };
      const response = await axios('http://localhost:5000/api/employees', options);
      console.log(response)
      console.log(55)
     
    }
  };

  render() {
    const { userName, password, name, showSubmitError, errorMsg, showLoginPage } = this.state;

    if (showLoginPage) {
      return <Navigate to='/login' replace={true} />;
    }

    return (
      <div className="container d-flex justify-content-center flex-column">
        <Link to='/login'>
          <button>Login</button>
        </Link>
        <form onSubmit={this.submitForm} className='container'>
          <legend>Sign In</legend>
          <div className="mb-3" style={{ width: '500px' }}>
            <label htmlFor="name" className="form-label">Name</label>
            <input type="text" id="name" className="form-control" placeholder="Name" onChange={this.onChangeName} value={name} />
          </div>
          <div className="mb-3" style={{ width: '500px' }}>
            <label htmlFor="userName" className="form-label">User Name</label>
            <input type="text" id="userName" className="form-control" placeholder="User Name" onChange={this.onChangeUserName} value={userName} />
          </div>
          <div className="mb-3" style={{ width: '500px' }}>
            <label className="form-label" htmlFor="password">Password</label>
            <input className="form-control" type="password" id="password" placeholder="Password" onChange={this.onChangePassword} value={password} />
          </div>
          <button type="submit" className="btn btn-primary">Sign In</button>
          {showSubmitError && <p style={{ color: 'red' }}>{errorMsg}</p>}
        </form>
      </div>
    );
  }
}

export default SignIn;
