import React, { useState, useContext } from 'react';
import './Login.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
//import Cookies from 'js-cookie';
import AuthContext from '../Context/AuthContext';

const LoginPage = () => {
  const [email, seEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { login, isAuthenticated, setIsAuthenticated} = useContext(AuthContext);
  
  const handleLogin = (jwtToken) => {
    login(jwtToken, role)
    setIsAuthenticated(true)
    navigate('/')
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const apiUrl = `${process.env.REACT_APP_API_URL}/api/auth/login`;
      const response = await axios.post(apiUrl, { email, password });
      console.log('Response:', response.data.token);
      const jwtToken = response.data.token
      const role = response.data.role
      if (response.status === 200) {
        handleLogin(jwtToken, role)
      } else {
        setError(response.data.message || 'Login failed');
      }
    } catch (error) {
      console.error('Error:', error);
      setError(error.response.data.message || 'An error occurred');
    }
  };
  const onClickSignin = () => {
    navigate('/signin')
  }
  return (
    <div className="login-page">
      <div className="login-container">
        <h2>Welcome Back</h2>
        <form onSubmit={handleSubmit} className='form'>
          <div className="input-group">
            <input
              type="text"
              id="email"
              value={email}
              onChange={(e) => seEmail(e.target.value)}
              required
              placeholder="email"
            />
          </div>
          <div className="input-group">
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Password"
              
            />
          </div>
          <button type="submit" className="login-button">
            Login
          </button>
        </form>
        <button className="login-button" style={{ backgroundColor: '#e2a54a' }} onClick={onClickSignin}>Sign Up</button>
        {error && <p className="error-message">{error}</p>}
      </div>
    </div>
  );
};

export default LoginPage;
