import './Header.css';
import { React, useContext, useEffect, useState } from 'react';
import AuthContext from '../../Context/AuthContext';
import Cookies from 'js-cookie';
import EmployeeForm from './../Admin/EmployeeForm';
const Header = () => {
  const { logout } = useContext(AuthContext);
  const [userAdmin, setUserAdmin] = useState(null)
  useEffect(() => {
    const fetchUser = async () => {
      const jwtToken = Cookies.get('jwt_token');
      const url = `${process.env.REACT_APP_API_URL}/api/auth/user`
      const options = {
        headers: {
          Authorization: `Bearer ${jwtToken}`,
        },
        method: 'GET',
      }
      const response = await fetch(url, options)
      if (response.ok === true) {
        setUserAdmin(true)
      }
    };

    fetchUser();
  }, []);

  return (
    <>
      <header className="header">
        <div className="logo">
          <h1>YourLogo</h1>
        </div>
        <nav className="nav-menu">
          <ul>
            <li><a href="/">Home</a></li>
            <li><a href="/">User Info</a></li>
            <li><a href="/about">About</a></li>
            
          </ul>

        </nav>
        {userAdmin && (
              <div className="btn-group">
              <button type="button" className="btn btn-primary dropdown-toggle" data-bs-toggle="dropdown" data-bs-display="static" aria-expanded="false">
              Admin Panel
              </button>
              <ul className="dropdown-menu dropdown-menu-end dropdown-menu-lg-start">
                <li><button className="dropdown-item" type="button">Employee </button></li>
                <li><button className="dropdown-item" type="button">Another action</button></li>
                <li><button className="dropdown-item" type="button">Something else here</button></li>
              </ul>
            </div>
            )}
        <button className="logout-btn" onClick={logout}>Logout</button>
      </header>
    </>
  );
};

export default Header;
