import React, { Component } from 'react';
import Cookies from 'js-cookie';
import Headers from '../Header/Header';

class EmployeeUpdate extends Component {
  state = {
    name: '',
    position: '',
    department: '',
    id: ''
  };

  onChangeId = (event) => {
    this.setState({ id: event.target.value });
  }

  onChangeName = (event) => {
    this.setState({ name: event.target.value });
  }

  onChangePosition = (event) => {
    this.setState({ position: event.target.value });
  }

  onChangeDepartment = (event) => {
    this.setState({ department: event.target.value });
  }

  handleSubmitUpdate = async (event) => {
    event.preventDefault();
    const { name, position, id, department } = this.state;
    const employeeDetails = { name, position, department };
    const jwtToken = Cookies.get('jwt_token');
    const options = {
      method: 'PUT',
      headers: {
        Authorization: `Bearer ${jwtToken}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(employeeDetails)
    };

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/employees/${id}`, options);
      if (!response.ok) {
        throw new Error('Response put not ok');
      }
      alert(`Employee ID: ${id} updated successfully`);
      this.setState({ name: '', position: '', id: '', department: '' });
    } catch (error) {
      console.error(error);
    }
  }

  render() {
    const { name, position, id, department } = this.state;

    const containerStyle = {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      padding: '20px',
      backgroundColor: '#f8f9fa',
      minHeight: '100vh',
    };

    const formStyle = {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      width: '100%',
      maxWidth: '600px',
      padding: '20px',
      backgroundColor: '#fff',
      borderRadius: '8px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    };

    const headingStyle = {
      fontFamily: 'Roboto, sans-serif',
      color: '#333',
      marginBottom: '20px',
    };

    const labelStyle = {
      fontFamily: 'Roboto, sans-serif',
      color: '#333',
      fontWeight: 'bold',
      marginBottom: '5px',
      alignSelf: 'flex-start',
    };

    const inputStyle = {
      fontFamily: 'Roboto, sans-serif',
      color: '#333',
      padding: '10px',
      border: '1px solid #ccc',
      borderRadius: '5px',
      width: '100%',
      marginBottom: '15px',
    };

    const buttonStyle = {
      fontFamily: 'Roboto, sans-serif',
      fontWeight: 'bold',
      backgroundColor: '#28a745',
      borderColor: '#28a745',
      color: '#fff',
      padding: '10px 20px',
      borderRadius: '5px',
      border: 'none',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
    };

    const buttonHoverStyle = {
      backgroundColor: '#218838',
    };

    return (
      <div>
        <Headers />
        <div style={containerStyle}>
          <h2 style={headingStyle}>Update Employee</h2>
          <form onSubmit={this.handleSubmitUpdate} style={formStyle}>
            <div className="form-group" style={{ width: '100%' }}>
              <label htmlFor="id" style={labelStyle}>ID:</label>
              <input
                type="number"
                id="id"
                name="id"
                value={id}
                onChange={this.onChangeId}
                required
                style={inputStyle}
              />
            </div>
            <div className="form-group" style={{ width: '100%' }}>
              <label htmlFor="name" style={labelStyle}>Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={name}
                onChange={this.onChangeName}
                required
                style={inputStyle}
              />
            </div>
            <div className="form-group" style={{ width: '100%' }}>
              <label htmlFor="position" style={labelStyle}>Position:</label>
              <input
                type="text"
                id="position"
                name="position"
                value={position}
                onChange={this.onChangePosition}
                required
                style={inputStyle}
              />
            </div>
            <div className="form-group" style={{ width: '100%' }}>
              <label htmlFor="department" style={labelStyle}>Department:</label>
              <input
                type="text"
                id="department"
                name="department"
                value={department}
                onChange={this.onChangeDepartment}
                required
                style={inputStyle}
              />
            </div>
            <button
              type="submit"
              style={buttonStyle}
              onMouseOver={e => e.currentTarget.style.backgroundColor = buttonHoverStyle.backgroundColor}
              onMouseOut={e => e.currentTarget.style.backgroundColor = buttonStyle.backgroundColor}
            >
              Update Employee
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default EmployeeUpdate;
