import './Header.css';
import { React, useContext, useEffect, useState } from 'react';
import AuthContext from '../../Context/AuthContext';
import Cookies from 'js-cookie';

const Header = () => {
  const { logout } = useContext(AuthContext);
  const [userAdmin, setUserAdmin] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      const jwtToken = Cookies.get('jwt_token');
      const url = `${process.env.REACT_APP_API_URL}/api/auth/user`;
      const options = {
        headers: {
          Authorization: `Bearer ${jwtToken}`,
        },
        method: 'GET',
      };
      const response = await fetch(url, options);
      if (response.ok) {
        setUserAdmin(true);
      }
    };

    fetchUser();
  }, []);

  const logoUrl = 'https://i.postimg.cc/sxNqc8zX/pexels-sora-shimazaki-5673488.jpg'; // Path from the public directory

  return (
    <>
      <header className="header">
        <div className="logo">
          <img src={logoUrl} alt="Your Logo" style={{ height: '50px', width: 'auto' }} />
        </div>
        <nav className="nav-menu">
        <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
                <div class="navbar-nav ml-auto">
                    <a class="nav-link active" id="navItem1" href="#wcuSection">
                        Why Choose Us?
                        <span class="sr-only">(current)</span>
                    </a>
                    <a class="nav-link" href="#exploreMenuSection" id="navItem2">Explore Menu</a>
                    <a class="nav-link" href="#deliveryPaymentSection" id="navItem3">Delivery & Payment</a>
                    <a class="nav-link" href="#followUsSection" id="navItem4">Follow Us</a>
                </div>
            </div>
        </nav>
        {userAdmin && (
          <div className="btn-group">
            <button type="button" className="btn btn-primary dropdown-toggle" data-bs-toggle="dropdown" data-bs-display="static" aria-expanded="false">
              Admin Panel
            </button>
            <ul className="dropdown-menu dropdown-menu-end dropdown-menu-lg-start">
              <li><a className="dropdown-item" href="/employeecreate">Employee Create</a></li>
              <li><a className="dropdown-item" href="/employeeupdate">Employee Details Update</a></li>
              <li><a className="dropdown-item" href="/employeedelete">Employee Delete</a></li>
            </ul>
          </div>
        )}
        <button className="logout-btn" onClick={logout}>Logout</button>
      </header>
    </>
  );
};

export default Header;
